import React from "react";
import Header from "./Header";
import Footer from "./Footer";
import Main from "./Main";
import PopupWithForm from "./PopupWithForm";
import ImagePopup from "./ImagePopup";

function App() {
  const [isEditProfilePopupOpen, setIsEditProfilePopupOpen] = React.useState(false);
  const [isAddPlacePopupOpen, setIsAddPlacePopupOpen] = React.useState(false);
  const [isEditAvatarPopupOpen, setIsEditAvatarPopupOpen] = React.useState(false);
  const [isImageOpen, setIsImageOpen] = React.useState(false);
  const [selectedCard, setSelectedCard] = React.useState({});

  function handleCardClick(card) {
    setIsImageOpen(true);
    setSelectedCard(card);
  }

  function closeAllPopups() {
    setIsEditProfilePopupOpen(false);
    setIsAddPlacePopupOpen(false);
    setIsEditAvatarPopupOpen(false);
    setIsImageOpen(false);
  }

  function handleEditAvatarClick() {
    setIsEditAvatarPopupOpen(true);
  }

  function handleEditProfileClick() {
    setIsEditProfilePopupOpen(true);
  }

  function handleAddPlaceClick() {
    setIsAddPlacePopupOpen(true);
  }
  return (
    <div className="root">
      <div className="page">
        <Header/>
        <Main />
        <Footer />
        <PopupWithForm
        id={'profile'}
        title={'Редактировать профиль'}
        isOpen={isEditProfilePopupOpen}
        onClose={closeAllPopups}
        >
        <fieldset className="popup__fieldset">
          <input id="nameEdit-input" className="popup__input popup__input-name" type="text" name="name" value="Жак-Ив Кусто"
            minLength="2" maxlength="40" required />
          <span className="popup__error nameEdit-input-error"></span>
          <input id="jobEdit-input" className="popup__input popup__input-description" type="text" name="about"
            value="Исследователь океана" minLength="2" maxlength="200" required />
          <span className="popup__error jobEdit-input-error"></span>
        </fieldset>
       </PopupWithForm>

       <ImagePopup isOpen={isImageOpen} onClose={closeAllPopups} card={selectedCard}/>
       <PopupWithForm id={'delete'} title={'Вы уверены?'} isOpen={false} onClose={closeAllPopups}/>
       <PopupWithForm
        id={'avatar'}
        title={'Обновить аватар'}
        isOpen={isEditAvatarPopupOpen}
        onClose={closeAllPopups}
        >
          <fieldset className="popup__fieldset">
            <input id="urlAvatar-input" className="popup__input popup__input-avatar" type="url" name="avatar" value=""
              placeholder="Ссылка на картинку" required />
            <span className="popup__error urlAvatar-input-error"></span>
          </fieldset>
        </PopupWithForm>
      </div>
    </div>
  );
}

export default App;
