import React from "react";
import Header from "./Header";
import Footer from "./Footer";
import Main from "./Main";
import PopupWithForm from "./PopupWithForm";
// import ImagePopup from "./ImagePopup";

function App() {
  const [isEditProfilePopupOpen, setIsEditProfilePopupOpen] = React.useState(false);
  const [isAddPlacePopupOpen, setIsAddPlacePopupOpen] = React.useState(false);
  const [isEditAvatarPopupOpen, setIsEditAvatarPopupOpen] = React.useState(false);
  const [isImageOpen, setIsImageOpen] = React.useState(false);
  const [selectedCard, setSelectedCard] = React.useState({});

  function handleCardClick(card) {
    setIsImageOpen(true);
    setSelectedCard(card);
  }

  function closeAllPopups() {
    setIsEditProfilePopupOpen(false);
    setIsAddPlacePopupOpen(false);
    setIsEditAvatarPopupOpen(false);
    setIsImageOpen(false);
  }

  function handleEditAvatarClick() {
    setIsEditAvatarPopupOpen(true);
  }

  function handleEditProfileClick() {
    setIsEditProfilePopupOpen(true);
  }

  function handleAddPlaceClick() {
    setIsAddPlacePopupOpen(true);
  }
  return (
    <div className="root">
      <body className="page">
        <Header/>
        <Main />
        <Footer />
        <PopupWithForm 
        id={'profile'}
        title={'Редактировать профиль'}
        isOpen={isEditProfilePopupOpen}
        onClose={closeAllPopups}
        >
        <fieldset className="popup__fieldset">
          <input id="nameEdit-input" className="popup__input popup__input-name" type="text" name="name" value="Жак-Ив Кусто"
            minlength="2" maxlength="40" required />
          <span className="popup__error nameEdit-input-error"></span>
          <input id="jobEdit-input" className="popup__input popup__input-description" type="text" name="about"
            value="Исследователь океана" minlength="2" maxlength="200" required />
          <span className="popup__error jobEdit-input-error"></span>
        </fieldset>
       </PopupWithForm>

       <ImagePopup isOpen={isImageOpen} onClose={closeAllPopups} card={selectedCard}/>
       <PopupWithForm id={'delete'} title={'Вы уверены?'} isOpen={false} onClose={closeAllPopups}/>



  <div className="popup popup_type_add">
    <div className="popup__container">
      <button className="popup__close" type="button"></button>
      <h2 className="popup__title">Новое место</h2>
      <form novalidate className="popup__form" name="form-add">
        <fieldset className="popup__fieldset">
          <input id="nameAdd-input" className="popup__input popup__input-name" type="text" name="name" value=""
            placeholder="Название" minlength="2" maxlength="30" required />
          <span className="popup__error nameAdd-input-error"></span>
          <input id="urlEdit-input" className="popup__input popup__input-description" type="url" name="link" value=""
            placeholder="Ссылка на картинку" required />
          <span className="popup__error urlEdit-input-error"></span>
        </fieldset>
        <button className="popup__button">Создать</button>
      </form>
    </div>
  </div>
  <div className="popup popup_type_picture">
    <div className="popup__container-picture">
      <figure className="popup__figure">
        <button className="popup__close" type="button"></button>
        <img className="popup__img" />
        <figcaption className="popup__figure-caption"></figcaption>
      </figure>
    </div>
  </div>
  <div className="popup popup_type_delete">
    <div className="popup__container">
      <button className="popup__close" type="button"></button>
      <h2 className="popup__title popup__title_delete">Вы уверены?</h2>
      <form className="popup__form" name="deleteCard">
        <button className="popup__button popup__button_delete" type="submit">Да</button>
      </form>
    </div>
  </div>
  <div className="popup popup_type_avatar">
    <div className="popup__container">
      <button className="popup__close" type="button"></button>
      <h2 className="popup__title">Обновить аватар</h2>
      <form novalidate className="popup__form" name="form-avatar">
        <fieldset className="popup__fieldset">
          <input id="urlAvatar-input" className="popup__input popup__input-avatar" type="url" name="avatar" value=""
            placeholder="Ссылка на картинку" required />
          <span className="popup__error urlAvatar-input-error"></span>
          <button className="popup__button" type="submit">Сохранить</button>
        </fieldset>
      </form>
    </div>
  </div>
  <template id="card">
    <div className="element">
      <img className="element__img" />
      <button className="element__delete" type="button"></button>
      <div className="element__caption">
        <h3 className="element__title"></h3>
        <div className="element__like-container">
          <button className="element__like" type="button"></button>
          <span className="element__like-quantity"></span>
        </div>
      </div>
    </div>
  </template>
      </body>
    </div>
  );
}

export default App;
