import React from "react";
// import {api} from "../utils/Api";
// import Card from "./Card";

function Main (props) {
    const [userName, setUserName] = React.useState('');
    const [userDescription, setUserDescription] = React.useState('');
    const [userAvatar, setUserAvatar] = React.useState('');
    const [cards, setCards] = React.useState([]);


    return (
    <main className="content">
        <section className="profile">
          <button className="profile__avatar-edit">
            <img className="profile__avatar" src={userAvatar} alt={userName} onClick={props.onEditAvatar} />
          </button>
          <div className="profile__info">
            <div className="profile__name-container">
              <h1 className="profile__name">{userName}</h1>
              <button className="profile__button-edit" type="button" onClick={props.onEditProfile}>
                <img className="profile__button-edit-img" src="<%=require('./images/edit_button.svg')%>"
                  alt="Кнопка изменить" />
              </button>
            </div>
            <p className="profile__description">{userDescription}</p>
          </div>
          <button className="profile__button-add" type="button" onClick={props.onAddPlace}></button>
        </section>
        <section className="elements">
            <ul className="cards">
            {
                cards.map((card) => (
                <Card
                    key={card._id}
                    card={card}
                    title={card.name}
                    likeCounter={card.likes.length}
                    image={card.link}
                    onCardClick={props.onCardClick}
                />
                ))}
            </ul>
        </section>
    </main>
    )
  }
  
  export default Main;
