import { Link } from "react-router-dom";
import React from "react";

export default function Register({ registerUser, buttonText }) {
  const [formValue, setFormValue] = React.useState({
    email: "",
    password: "",
  });

  function handleChange(e) {
    const input = e.target;
    setFormValue({
      ...formValue,
      [input.name]: input.value,
    });
  }

  function handleSubmit(e) {
    e.preventDefault();
    registerUser(formValue);
  }

  return (
    <div className="ident">
      <p className="ident__welcome">Регистрация</p>
      <form onSubmit={handleSubmit} name="register" className="ident__form">
        <input
          name="email"
          type="email"
          className="input"
          value={formValue.email}
          placeholder="Email"
          onChange={handleChange}
          required
        />
        <span className="popup__error popup__error_active"></span>
        <input
          name="password"
          type="password"
          className="input"
          value={formValue.password}
          placeholder="Пароль"
          onChange={handleChange}
          required
        />
        <span className="popup__error popup__error_active"></span>
        <button type="submit" className="ident__link">
          {buttonText}
        </button>
      </form>
      <Link to="/sign-in" className="register__login__link">
        Уже зарегистрированы? Войти
      </Link>
    </div>
  );
}
